import React from 'react'
import Cards from './Cards';
import Tag from './Tag';
import {StarIcon} from '@heroicons/react/outline';


const Trending = () => {
    let comicsInfo =[
        {id:"1",title:"Civil War II",starRating: "2.34" ,house:"Marvel",year: "2016",genre: "CrossOver",runtime:"1h",cast:"Iron Man | Captain Marvel",image:"https://readcomicsonline.ru/uploads/manga/civil-war-ii-2016/chapters/1/01.jpg"},
        {id:"2",title:"NEW RELEASES",starRating: "2.34" ,house:"Marvel",year: "2016",genre: "CrossOver",runtime:"1h",cast:"Iron Man | Captain Marvel",image:"https://readcomicsonline.ru/uploads/manga/civil-war-ii-2016/chapters/1/01.jpg"},
        {id:"3",title:"NEW RELEASES",starRating: "2.34" ,house:"Marvel",year: "2016",genre: "CrossOver",runtime:"1h",cast:"Iron Man | Captain Marvel",image:"https://readcomicsonline.ru/uploads/manga/civil-war-ii-2016/chapters/1/01.jpg"},
        {id:"4",title:"NEW RELEASES",starRating: "2.34" ,house:"Marvel",year: "2016",genre: "CrossOver",runtime:"1h",cast:"Iron Man | Captain Marvel",image:"https://readcomicsonline.ru/uploads/manga/civil-war-ii-2016/chapters/1/01.jpg"},

      ]; 
  return (
    <div class="py-6 px-4 sm:p-6 md:py-10 md:px-8 ">
        <div class="max-w-7xl mx-auto flex flex-row overflow-x-auto gap-5">
    {comicsInfo.map((comic)=>(

                <div class="flex-none">
                    <div class="flex flex-col md:flex-row md:max-w-xl rounded-lg bg-white shadow-lg dark:bg-zinc-500 ">
                        <img class=" w-full h-96 md:h-auto object-cover md:w-48 rounded-t-lg md:rounded-none md:rounded-l-lg" src={comic.image} alt="" />
                        <div class="p-6 flex flex-col justify-start">
                        <h5 class="text-gray-900 text-xl mb-2 font-bold dark:text-white flex">{comic.title} <Tag>NEW!</Tag> </h5>
                        <p class="text-gray-700 text-base mb-4 font-thin dark:text-white">
                            This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.
                        </p>
                        <p class="text-gray-600 text-xs dark:text-white"><StarIcon/></p>
                        </div>
                    </div>
                </div>

    ))}
        </div>
    </div>


  )
}

export default Trending
