import React from 'react';
import {ReactComponent as Logo} from '../logo.svg';
import {MenuIcon, XIcon} from '@heroicons/react/outline';
import Button from './Button';
import {useState} from 'react';




const Navbar = () =>{
  let Links =[
    {name:"NEW RELEASES", link:"/"},
    {name:"DC", link:"/"},
    {name:"MARVEL", link:"/"},
    {name:"SING-IN", link:"/"},
  ]; 
  let [open,setOpen]=useState(false);
  return (
   /* <div className='w-screen md:w-screen flex h-[80px] z-10 bg-white fixed drop-shadow-lg'>
      <div className='mx-2 flex gap-10 items-center w-full h-full '>
          <Logo className='w-48' />
      </div>
      <div className='mx-2 flex gap-10 items-center w-full h-full justify-end'>
        <h1>New releases</h1>
        <h1>DC</h1>
        <h1>Marvel</h1>
        <h1 className='btn btn-blue'>Login</h1>
      </div>

    </div>*/ 
    <div className='shadow-md w-full top-0 left-0'>
      <div className='md:flex items-center justify-between bg-white py-4 md:px-10 px-7 dark:bg-zinc-700'>
        <div className='cursor-pointer flex items-center dark:dark:text-white'>
          <Logo className='h-10 m-0 p-0 w-10'/>
        </div>
        <div onClick={()=>setOpen(!open)}
        className='text-3xl absolute right-8 top-5 cursor-pointer md:hidden'>
          <MenuIcon name={open ? 'close':'menu'} className="h-5 w-5 dark:text-white"/>
        </div>
        <ul className={`md:flex md:items-center md:pb-0 pb-12 absolute md:static bg-white md:z-auto z-[1] left-0
        w-full md:w-auto md:pl-0 pl-9 transition-all duration-500 ease-in ${open ? 'top-19 opacity-100':'top-[-490px] md:opacity-100 opacity-0'} dark:bg-zinc-700`}>
          {
            Links.map((link)=>(
              <li key={link.name} className="md:ml-8 text-base md:my-0 my-7">
                <a href={link} className="text-gray-800 hover:text-gray-400 duration-500 dark:text-white">{link.name}</a>
              </li>
            ))
          }
          <Button>Sign-in</Button>
        </ul>
      </div>
    </div>


  )
}

export default Navbar
